import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";

import { recipeService } from "../services";

const Details = () => {

  const params = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    // console.log(match.params.recipeId)
    // console.log(params)
    recipeService(params.recipeId)
      .then(res => {
        console.log(res._details)
        setRecipe(res._details)
      })
  }, [])

  return (<div>

    {recipe && <div>
    <div class="card mb-3">
      <img class="card-img-top" src={recipe.imageURL} alt={recipe.name} />
      <div class="card-body">
        <h5 class="card-title">{recipe.name}</h5>
        <p class="card-text">{recipe.ingredients}</p>
        <p class="card-text">{recipe.steps}</p>
        <p class="card-text">{recipe.category}</p>
        <a href="#" class="btn btn-primary">Go somewhere</a>
        <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
      </div>
    </div> 
    </div>}


  </div>)

}

export default Details;
