import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Spinner from 'react-bootstrap/Spinner';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import '../css/login.css'
import Card from 'react-bootstrap/Card';

// import { serviceLogin } from "../../services";

const Login = () => {

    // const navigate = useNavigate();

    const [emailAddress, setEmailAddress] = useState('');
    const [password, setPassword] = useState('');

    const [error, setError] = useState(false);
    const [errorStatus, setErrorStatus] = useState('');
    const [errorMessage, setErrorMessage] = useState(false);

    const [success, setSuccess] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');


    const submit = (e) => {
        e.preventDefault()
        const data = { email: emailAddress, password }
        // console.log(data)
        // const response = serviceLogin(data);
        response.then((data) => {
            if (data.success) {
                setSuccess(true);
                setSuccessMessage(data.message);
                // setTimeout(() => {
                //     navigate('/v1/admin');
                // }, 5000);
            } else {
                setSuccess(false);
            }
            // console.log(data)
            if (data.error) {
                setError(true);
                setErrorStatus(data.errorStatus);
                setErrorMessage(data.errorMessage);
            } else {
                setError(false);
            }
        })
    }

    return (<div>
        <div className='register-form'>
            <Card style={{ width: '100%' }}>
                <Card.Body>
                    <h3 className='mb-3 text-center'>USER LOGIN</h3>
                    {success ? <div className='mb-3 text-center text-success'><Spinner animation="grow" /></div> : ""}
                    {error ? <p className='text-center text-danger'>{errorMessage}</p> : ""}
                    {errorStatus === 401 ? <Link className='mb-3 text-center' to={'/v1/verifyemail'} >Verify Mail</Link> : ""}
                    {success ? <p className='mb-3 text-center text-success'>{successMessage}</p> : ""}

                    <div>
                        <Form onSubmit={submit}>

                            <Form.Group className="mb-3" >
                                <Form.Label>Email address</Form.Label>
                                <Form.Control
                                    type="email"
                                    value={emailAddress}
                                    onChange={(event) => setEmailAddress(event.target.value)
                                    }
                                    placeholder="Enter email"
                                />
                                <Form.Text className="text-muted">
                                    We'll never share your email with anyone else.
                                </Form.Text>
                            </Form.Group>

                            <Form.Group className="mb-3" >
                                <Form.Label>Password</Form.Label>
                                <Form.Control
                                    type="password"
                                    value={password}
                                    onChange={(event) => setPassword(event.target.value)
                                    }
                                    placeholder="Password"
                                />
                            </Form.Group>
                            <Button variant="primary" type="submit">
                                Login
                            </Button>
                        </Form>
                    </div>

                </Card.Body>
            </Card>
        </div>
    </div>);
}

export default Login;
