import axios from "axios";
const BASE_URI = "http://localhost:8081";

export const recipesService = async () => {
    try {
        const res = await axios.get(`${BASE_URI}/login`);
        if (res.data.success) {
            return res.data;
        }
    } catch (error) {
        console.log(error)
    }
}


export const registerService = async (data) => {
    try {
        const res = await axios.post(`${BASE_URI}/register`, data);
        // console.log(res)
        if (res.data.success) {
            // console.log(res.data)
            alert(res.data.message);
            localStorage.setItem('user', JSON.stringify(res.data._details))
            return res.data;
        }
    } catch (error) {
        alert(error.response.data.message)
    }
}
