import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";

import { recipeService } from "../services";

const Details = () => {

  const params = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    // console.log(match.params.recipeId)
    // console.log(params)
    recipeService(params.recipeId)
      .then(res => {
        console.log(res._details)
        setRecipe(res._details)
      })
  }, [])

  return (<div>

    {recipe && <div>
      <div class="card mb-3" style={{ border: 'none' }}>
        <img class="card-img-top" style={{ height: "420px" }} src={recipe.imageURL} alt={recipe.name} />
        <div class="card-body">
          <h5 class="card-title">{recipe.name}</h5>
          <p class="card-text">{recipe.ingredients && Object.keys(recipe.ingredients).map(item => <div>
            
            <ul><li>
              Name: {recipe.ingredients[item].name}<br />
              Type: {recipe.ingredients[item].type}<br />
              Quantity: {recipe.ingredients[item].quantity}<br />
            </li></ul>
          </div>)}</p>
          <p class="card-text">{recipe.steps}</p>
          <p class="card-text">{recipe.category}</p>
          <a href="#" class="btn btn-primary">Go somewhere</a>
        </div>
      </div>
    </div>}


  </div>)

}

export default Details;
