import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';


import { Link, useNavigate } from "react-router-dom";

import { recipesService, addrecipeService } from "../services";

const FoodRecipes = () => {

  const navigate = useNavigate();

  const [recipe, setRecipe] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [tempData, setTempData] = useState([]);

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    recipesService().then(res => {
      console.log(res._recipes)
      setRecipes(res._recipes);
      setTempData(res._recipes);
    })
  }, [])

  const logout = () => {
    localStorage.removeItem("user");
    navigate('/');
  };

  const [recipeForm, setRecipeForm] = useState({
    name: '',
    category: '',
    description: '',
    step: '',
    ingredient: {
      quantity:'',
      name:'',
      type:''
    },
    imageURL: '',
  });

  const [steps, setSteps] = useState([]);
  const [ingredients, setIngredients] = useState([]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setRecipeForm((prevProps) => ({
      ...prevProps,
      [name]: value
    }));
  };
  
  const _handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      setSteps([...steps, recipeForm.step]);
      setRecipeForm((prevProps) => ({
        ...prevProps,
        ['step']: ''
      }));
    }
  }

  const handleSubmitRecipe = (event) => {
    event.preventDefault();
    if (recipeForm.name &&
      recipeForm.category &&
      recipeForm.description &&
      recipeForm.imageURL &&
      steps.length > 1
    ) {
      console.log(recipeForm);
      console.log(steps);
      const recipeData = {
        name: recipeForm.name,
        category: recipeForm.category,
        description: recipeForm.description,
        steps: steps,
        ingredients: {},
        imageURL: recipeForm.imageURL,
      }
      addrecipeService(recipeData).then(res => {
        console.log(res)
      })
    }
  };

  return (<div>
    <h3 style={{ fontWeight: 'bold', textAlign: 'center' }}>SEARCH & ADD RECIPES HERE !!</h3>
    <div className='p-1 mt-3 d-flex justify-content-between '>
      {/* <div> */}
      <div>
        <Button variant="primary" onClick={handleShow}>
          Add Recipe
        </Button>
      </div>
      {/* <div>Food Recipe</div> */}
      <Form.Group className="mb-3" style={{ width: "350px" }} >
        <Form.Control
          type="text"
          value={recipe}
          onChange={(event) => {
            setRecipe(event.target.value)
            // console.log(recipes.filter(item =>
            //   item.name.toLowerCase().indexOf(event.target.value.toLowerCase()) > -1
            // ))
            setTempData(recipes.filter(item =>
              item.name.toLowerCase()
                .indexOf(event.target.value.toLowerCase()) > -1
            ))
          }}
          placeholder="Enter Recipe"
        />
      </Form.Group>
      <div>
        <Button variant="primary" type="button" onClick={logout} >
          Logout
        </Button>
      </div>
    </div>
    <div>
      <h5 style={{ marginLeft: '15px' }}>RECIPES</h5>
      <div className='row p-0 m-0'>

        {
          tempData &&
          tempData.length > 0 &&
          Object.keys(tempData).map(item =>
            <div className='col-4 p-1 d-flex justify-content-center'>
              <div class="card" style={{ width: '18rem' }}>
                <img class="card-img-top" style={{ height: '180px', objectFit: 'cover' }} src={tempData[item].imageURL} alt={tempData[item].name} />
                <div class="card-body">
                  <h5 class="card-title item-line">{tempData[item].name}</h5>
                  <Link to={`/details/${tempData[item]._id}`} >Checkout</Link>
                </div>
              </div>
            </div>
          )
        }
      </div>
      {/* <div>{tempData[item]._id}</div> */}
    </div>
    {/* </div> */}

    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>ADD RECIPE</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmitRecipe}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Recipe name</Form.Label>
            <Form.Control type="text" placeholder="Enter recipe name" name="name"
              value={recipeForm.name}
              onChange={handleInputChange} />
            {/* <Form.Text className="text-danger">
              Please add recipe name, name is required!
            </Form.Text> */}
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Category</Form.Label>
            <Form.Control type="text" placeholder="Enter recipe category" name="category"
              value={recipeForm.category}
              onChange={handleInputChange} />
            {/* <Form.Text className="text-danger">
              Please add recipe category, category is required!
            </Form.Text> */}
          </Form.Group>

          <Form.Label>Ingredients</Form.Label>
          <Button variant="primary" type="button">
            Add
          </Button>

          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Steps</Form.Label>
            <Form.Control type="text" placeholder="Enter recipe steps" name="step"
              value={recipeForm.step}
              onChange={handleInputChange}
              onKeyDown={_handleKeyDown}
            />
            {/* onKeyDown={_handleKeyDown} */}
            {/* <Form.Text className="text-danger">
              Please add recipe image url, image url is required!
            </Form.Text> */}
          </Form.Group>

          <ul>
            {steps && steps.map((item, index) => <li>Step {index + 1}: {item}</li>)}
          </ul>

          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Description</Form.Label>
            <Form.Control type="text" placeholder="Enter recipe description" name="description"
              value={recipeForm.description}
              onChange={handleInputChange} />
            {/* <Form.Text className="text-danger">
              Please add recipe description, description is required!
            </Form.Text> */}
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Image URL</Form.Label>
            <Form.Control type="text" placeholder="Enter recipe image url" name="imageURL"
              value={recipeForm.imageURL}
              onChange={handleInputChange} />
            {/* <Form.Text className="text-danger">
              Please add recipe image url, image url is required!
            </Form.Text> */}
          </Form.Group>

          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </Modal.Body>
    </Modal>

  </div>)
}
export default FoodRecipes;
